import React from "react";
import { useQuery } from "@apollo/react-hooks";
import gql from "graphql-tag";
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";

import NoteCard from "../components/card/card.component";
import NoteInputCard from "../components/card-grid/card-grid.component";

const useStyles = makeStyles((theme) => ({
  root: {
    padding: theme.spacing(1),
    flexGrow: 1,
  },
  paper: {
    // padding: theme.spacing(1),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
}));

const Home = () => {
  const { loading, data } = useQuery(FETCH_POST_QUERY);
  if (data) {
    console.log(data.getNotes[0]);
  }
  return loading ? (
    <h1> Notes loading</h1>
  ) : (
    <div className={classes.root}>
      <Grid container justify="center" alignItems="center">
        <NoteInputCard />
      </Grid>
      <Grid container spacing={1}>
        {data.getNotes.map((note) => {
          return (
            <Grid item xs={4}>
              <Paper className={classes.paper}>
                <NoteCard title="title" noteText={note.body} modelView={open} />
              </Paper>
            </Grid>
          );
        })}
      </Grid>
    </div>
  );
};

const FETCH_POST_QUERY = gql`
  {
    getNotes {
      id
      body
      createdAt
      username
    }
  }
`;

export default Home;
